int main()
{
	InitialConsole();//초기화함수
	SetWindowTitle("Shooting");

	while (g_CMng.m_bLoop)
	{
		CheckInput();
		ClearScreenEx3(BLACK);

		if (IsKey(VK_ESCAPE))
		{
			g_CMng.m_bLoop = 0;
		}
		if (IsKey(VK_H))
		{
			g_CMng.m_CStateCtrl.StateChange(E_MENU);
		}

		g_CMng.Update();
		g_CMng.Draw();

		Flip();
		EngineSync(30);
	}


	/*	Console 셋팅을 해제한다.									*/
	ReleaseConsole();
	return 0;
}
